                   tags$ul(
                     tags$li("Percentage of purchases from non-vessel sources"),
                     tags$li("Percentage of production processed"),
                     tags$li("Proportion of Purchase Weight from West Coast groundfish purchased by Catch Share Processors"),
                     tags$li("Proportion of Purchase Value from West Coast groundfish purchased by Catch Share Processors"))
                 )
